import styled from "styled-components";

export const Container = styled.div`
    h1{
        text-align: center;
        margin: 4rem 0;
    }

    ul{
        list-style: none;
        display: grid;
        grid-template-coluns: repeat (3, 1fr); 
        colum-app: 3rem;
        row-gap: 4rem;
    }

    li {
        display: flex;
        flex-direction: colum;
        align-items
        }
    }

`
