import styled from "styled-components";

export const Container = styled.div`
    h1{
        text-align: center;
        margin: 4rem 0;
    }

    ul{
        list-style: none;
        display: grid;
        grid-template-columns: repeat(auto-fit, minmax(200px, 1fr)); 
        column-app: 3rem;
        row-gap: 4rem;
    }

   
`

export const MovieList = styled.ul`

ul{
    list-style: none;
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr)); 
    column-app: 3rem;
    row-gap: 4rem;
}



`
